declare class Scanner {
    text: any;
    pos: any;
    tail: any;
    constructor(text: any);
    /**
     * 路过指定内容
     *
     * @memberof Scanner
     */
    scan(tag: any): void;
    /**
     * 让指针进行扫描，直到遇见指定内容，返回路过的文字
     *
     * @memberof Scanner
     * @return str 收集到的字符串
     */
    scanUntil(stopTag: any): any;
    /**
     * 判断指针是否到达文本末尾（end of string）
     *
     * @memberof Scanner
     */
    eos(): boolean;
}
export default Scanner;
